-- Add status column to project_scores if it doesn't exist
SET @sql = IF((SELECT COUNT(*) FROM information_schema.COLUMNS 
  WHERE TABLE_SCHEMA = DATABASE() AND TABLE_NAME = 'project_scores' AND COLUMN_NAME = 'status') = 0,
  'ALTER TABLE project_scores ADD COLUMN status ENUM(''draft'',''submitted'',''locked'') DEFAULT ''draft''',
  'SELECT "status already exists" as message');
PREPARE stmt FROM @sql;
EXECUTE stmt;
DEALLOCATE PREPARE stmt;

